import { useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import "animate.css";
import TrackVisibility from "react-on-screen";

export const Banner = () => {
  useEffect(() => {
    document.documentElement.style.setProperty('--animate-duration', '1.2s');
  }, []);

  return (
    <section className="banner" id="home">
      <Container>
        <Row className="align-items-center">
          <Col xs={12}>
            <TrackVisibility partialVisibility>
              {({ isVisible }) => (
                <div className="hero-text">
                  <h1 className={`name-title ${isVisible ? "animate__animated animate__nameEntrance" : ""}`}>
                    Rushik<br/>Behal
                  </h1>
                  <p className={`subtitle ${isVisible ? "animate__animated animate__fadeInUp" : ""}`}>
                    I'm Rushik Behal â€” a Computer Science Student at<br/>
                    Simon Fraser University, Canada!
                  </p>
                  <div className={`scroll-indicator ${isVisible ? "animate__animated animate__fadeIn" : ""}`}>
                    SCROLL
                  </div>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
