import { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Contact = () => {
  const formInitialDetails = {
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    message: ''
  }
  const [formDetails, setFormDetails] = useState(formInitialDetails);
  const [buttonText, setButtonText] = useState('Send');
  const [status, setStatus] = useState({});

  const onFormUpdate = (category, value) => {
      setFormDetails({
        ...formDetails,
        [category]: value
      })
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    setButtonText("Sending...");
    let response = await fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(formDetails),
    });
    setButtonText("Send");
    let result = await response.json();
    setFormDetails(formInitialDetails);
    if (result.code == 200) {
      setStatus({ succes: true, message: 'Message sent successfully'});
    } else {
      setStatus({ succes: false, message: 'Something went wrong, please try again later.'});
    }
  };

  return (
    <section className="contact" id="connect">
      <Container>
        <Row className="align-items-center">
          {/* Left Column - Text Box */}
          <Col md={6} className="d-flex align-items-center">
            <TrackVisibility partialVisibility once>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <div className="contact-text-box">
                    <h2 className="mb-4">Let's Connect!</h2>
                    <p className="mb-3">
                      I'm always open to new opportunities and meaningful collaborations. 
                      Whether you have a project in mind, a question about my work, 
                      or just want to say hello - feel free to reach out!
                    </p>
                    <p className="mb-0">
                      I typically respond within 24 hours and would be happy to 
                      discuss how we can work together to bring your ideas to life.
                    </p>
                  </div>
                </div>
              )}
            </TrackVisibility>
          </Col>

          {/* Right Column - Contact Form */}
          <Col md={6}>
            <TrackVisibility partialVisibility once>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h2>Get In Touch</h2>
                  <form onSubmit={handleSubmit}>
                    {/* Rest of your form inputs remain the same */}
                    {/* ... existing form code ... */}
                  </form>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
