import React from 'react';

const GradientBackground = ({ children }) => {
  return (
    <div className="min-h-screen relative">
      {/* Base dark background */}
      <div className="fixed inset-0 bg-[#0B0B0F]" />

      {/* Top gradient overlay */}
      <div 
        className="fixed inset-0 opacity-40"
        style={{
          background: 'linear-gradient(180deg, rgba(55, 47, 89, 0.5) 0%, transparent 35%)',
        }}
      />

      {/* Bottom gradient overlay */}
      <div 
        className="fixed inset-0 opacity-30"
        style={{
          background: 'linear-gradient(360deg, rgba(74, 47, 189, 0.2) 0%, transparent 35%)',
        }}
      />

      {/* Subtle radial gradients for depth */}
      <div className="fixed inset-0">
        <div className="absolute top-0 left-1/4 w-full h-full bg-[radial-gradient(circle_at_center,rgba(74,47,189,0.08),transparent_50%)]" />
        <div className="absolute bottom-0 right-1/4 w-full h-full bg-[radial-gradient(circle_at_center,rgba(170,54,124,0.08),transparent_50%)]" />
      </div>

      {/* Content container */}
      <div className="relative z-10">
        {children}
      </div>
    </div>
  );
};

export default GradientBackground;
